Scripts.OnLoad:
CommitButton.activeElement = D32CharacterData.healthOrb local fillOptions = { "Default", "Combat" }
function HealthOrbOptionsDropDown:OnClick(value) UIDropDownMenu_SetSelectedID(HealthOrbOptionsDropDown,self:GetID()) local buttonText = UIDropDownMenu_GetText(HealthOrbOptionsDropDown) if buttonText == "Combat" then CommitButton.activeElement = D32CharacterData.combat UpdateXMLColors(CommitButton.activeElement.orbColor,CommitButton.activeElement.galaxy,CommitButton.activeElement.font1,CommitButton.activeElement.font2) elseif buttonText == "Default" then CommitButton.activeElement = D32CharacterData.healthOrb UpdateXMLColors(CommitButton.activeElement.orbColor,CommitButton.activeElement.galaxy,CommitButton.activeElement.font1,CommitButton.activeElement.font2) end end
local function initMen(self, level) local stuff = UIDropDownMenu_CreateInfo() for k,v in pairs(fillOptions) do stuff = {} stuff.text = v stuff.value = v stuff.func = HealthOrbOptionsDropDown.OnClick UIDropDownMenu_AddButton(stuff, level) end end UIDropDownMenu_Initialize(HealthOrbOptionsDropDown, initMen) UIDropDownMenu_SetSelectedID(HealthOrbOptionsDropDown, 1) UIDropDownMenu_JustifyText(HealthOrbOptionsDropDown, "LEFT")

function FillTexturesDropDown:OnClick(value) UIDropDownMenu_SetSelectedID(FillTexturesDropDown,self:GetID()) TryNewTextures(D32FillTextureChoices[self:GetID() - 1],nil) end
local textureNameTable = { [0] = "Moon", [1] = "Mist", [2] = "Liquid Metal", [3] = "Dragon Skin", [4] = "Iris", [5] = "Fractals", [6] = "Warped" }
local function fillMenu(self, level) local num = table.getn(textureNameTable) for i=0,num,1 do local val = textureNameTable[i] stuff = {} stuff.text = val stuff.value = val stuff.func = FillTexturesDropDown.OnClick UIDropDownMenu_AddButton(stuff, level) end end UIDropDownMenu_Initialize(FillTexturesDropDown, fillMenu) UIDropDownMenu_SetSelectedID(FillTexturesDropDown, 1) UIDropDownMenu_JustifyText(FillTexturesDropDown, "LEFT")

function RotationTexturesDropDown:OnClick(value) UIDropDownMenu_SetSelectedID(RotationTexturesDropDown,self:GetID()) TryNewTextures(xmlOrbDisplayFrame2.orb.filling:GetTexture(),D32RotationTextureChoices[self:GetID() - 1]) end
local textureNameTable2 = { [0] = "Galaxy", [1] = "Bubbles", [2] = "Eye Glow", }
local function rotationMenu(self, level) local num = table.getn(textureNameTable2) for i=0,num,1 do local val = textureNameTable2[i] stuff = {} stuff.text = val stuff.value = val stuff.func = RotationTexturesDropDown.OnClick UIDropDownMenu_AddButton(stuff, level) end end UIDropDownMenu_Initialize(RotationTexturesDropDown, rotationMenu) UIDropDownMenu_SetSelectedID(RotationTexturesDropDown, 1) UIDropDownMenu_JustifyText(RotationTexturesDropDown, "LEFT")
